window.addEventListener("DOMContentLoaded",o=>{if(window.location.hash){const t=document.querySelector(window.location.hash);t&&setTimeout(()=>{t.scrollIntoView()},500)}});const e=document.getElementById("nav"),l=document.getElementById("menu-mobile"),n=document.getElementById("mobile-modal");e.style.backgroundColor="transparent";e.style.maxHeight="0px";window.addEventListener("scroll",()=>{window.scrollY>=50?(e.style.backgroundColor="#000",e.style.maxHeight="200px",e.style.color="rgb(255, 208, 90)"):(e.style.backgroundColor="transparent",e.style.maxHeight="0px",e.style.color="white")});l.addEventListener("click",()=>{n.classList.toggle("disable")});
